                <h2>구경하러가기</h2>
                </Col>
            </Row>
            </Container>

            </div>)




    return (<div>
        <Title title="구매 목록" set_middle={false}></Title>
        <Container>

        <ListGroup>
            {orders}
        </ListGroup>
        </Container>


    </div>)
}

export default PurchaseList;
