import {ListGroup, Container, Button, Form, Row, Col} from 'react-bootstrap'
import axios from 'axios'
import {useEffect, useState} from 'react'
import {useSelector} from 'react-redux'
import Title from './Title'
import { Link } from 'react-router-dom'
import PurchasePage from './PurchasePage'
import ImageUploading from 'react-images-uploading';





function ProductDetail({match, history}){
    const [amount, setAmount] = useState(1)
    const {isLoggedIn, userData} = useSelector(state =>({
        isLoggedIn: state.user.isLoggedIn,
        userData: state.user.payload
    }))

    const[product,setProduct]= useState({
    })
    const fetchProduct= async ()=>{
        await axios.get('/apis/v1/product/' + match.params.number).then(res=> {
            setProduct({
            ...res.data.payload.payload,
            product_id: match.params.number
            });

        })
        .catch(e => {
            // 정보가 없을 때 처리
            alert('없는 상품 정보')
        })
    }
    useEffect(()=>{

            fetchProduct()

    },[match.params.number])

    const onClickOrder = () => {
        if(isLoggedIn === false || userData == null){
            alert("로그인 후 이용하세요.")
            history.replace('/login')
        }
        if(amount > product.quantity){
            alert("수량이 잘못 되었습니다. ")
            return; 
        }

    }

    const onClickWishList = () => {
        if(isLoggedIn === false){
            alert("로그인 후 이용하세요.")
            return;
        }


    }

    const onChangeHandler = (e) => {
        const {value} = e.target
        if(value < 1){
            alert("올바르지 않습니다.")
            return;
        }

        setAmount(parseInt(value))



    }
    // 이미지 처리

    const [images, setImages] = useState([]);
    const maxNumber = 2;

    const onChangeImage = (imageList, addUpdateIndex) => {
      console.log(imageList, addUpdateIndex);
      setImages(imageList);
    };

    // 추가
    const onError = (errors, files) => {
      if(errors.maxNumber) {
        alert(`이미지는 ${maxNumber}개까지만 첨부할 수 있습니다`)
      }
    }

    const onClick = async () => {

      let body = {
        product_id: 2,
        ...images
      }
      console.log(body)
      await axios.post("/apis/image/product",body).then(res => {
        console.log(res)
      })
      .catch(e => {
        console.log(e)
      })

    }
    const onClickGetImage = async () => {

      let imageList = await axios.get("/apis/image/product").then(res => {

        return(res.data.map(data => {
          return {
            data_url: data.fields.base64_image_url}
        }))
      })
      .catch(e => {
        console.log(e)
      })

      console.log(imageList)

    }




    return(
        <div>
        <Title title= {product.name}></Title>
        <Container>
            <Row className="justify-content-md-center">
                <Col xs lg="8">
                <ListGroup>
            <ListGroup.Item>상품 카테고리: {product.category}</ListGroup.Item>
            <ListGroup.Item>상품명: {product.name}</ListGroup.Item>
            <ListGroup.Item>상품 가격: {product.price}</ListGroup.Item>
            <ListGroup.Item>상품 수량: {product.quantity}</ListGroup.Item>
            <ListGroup.Item>상품 설명: {product.description}</ListGroup.Item>
            <ListGroup.Item>상품 등록일:{product.created_at}</ListGroup.Item>
            </ListGroup>
        <br/>
        <p>구매 수량</p>
        <input type='number' onChange={onChangeHandler}value={amount}></input>
        <br/>
        <br/>
        <Button onClick={onClickWishList}>장바구니</Button>{' '}

        <Link to={isLoggedIn?{
            pathname: `/purchase`,
            state: {
                product:product,
                demand_amount:amount,
            }
          }:'/product/' + match.params.number}><Button onClick={onClickOrder}>구매하기</Button></Link>
                </Col>
            </Row>



        {/* 이미지 처리             */}

        <Button onClick={onClickGetImage}>이미지 가져오기 </Button>
      <ImageUploading
        multiple
        value={images}
        onChange={onChangeImage}
        maxNumber={maxNumber}
        dataURLKey="data_url"
        onError={onError}			// 추가
        >
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps,
        }) => (
          // write your building UI
          <div className="upload__image-wrapper">
            <button
              style={isDragging ? { color: 'red' } : undefined}
              onClick={onImageUpload}
              {...dragProps}
            >
             사진추가
            </button>
            &nbsp;
           <button onClick={onImageRemoveAll}>Remove all images</button>
            {imageList.map((image, index) => (
              <div key={index} className="image-item">
                <img src={image['data_url']} alt="" width="500" />
                <div className="image-item__btn-wrapper">
                  <button onClick={() => onImageUpdate(index)}>수정</button>
                  <button onClick={() => onImageRemove(index)}>삭제</button>
                </div>
              </div>
            ))}
          </div>

        )}


      </ImageUploading>
      <div>
        <Button onClick={onClick}>submit</Button>
        </div>
        </Container>

        </div>

    )
}

export default ProductDetail
