import React, {useState} from 'react'
import {Container, Row, Col, Form, Button} from 'react-bootstrap'
import Title from './Title'
import {useDispatch, useSelector} from 'react-redux'
import axios from 'axios'
import { loginAction, logoutAction } from '../modules/user'
import { CategoryDirection } from './CategoryBanner'

function Profile({history}){
    const dispatch = useDispatch()
    const {isLoggedIn, userData} = useSelector(state =>({
        isLoggedIn: state.user.isLoggedIn,
        userData: state.user.payload
    }))

    console.log(userData)

    const [user, setUser] = useState({
        username: userData.username,
        email: userData.useremail,
        phone_number:userData.phone_number
    })
    const {username, email, phone_number} = user
    const user_id = userData.user_id

    const onChangeHandler = (e) => {
        const {name, value} = e.target;
        setUser({
            ...user,
            [name]: value
        })

    };
    const onClickEditHandler = async(e)=>{
        e.preventDefault();
        let body = {
            user_id: user_id,
            username: username,
            useremail: email,
            phone_number: phone_number,

        };
        await axios.post(`/apis/v1/user/${user_id}`, body)
        .then(response => {
            alert("사용자 정보 수정 완료")
            dispatch(loginAction(body))


        }).catch(e =>{
            alert("사용자 정보 수정 실패")
        })
    }

    const onClickDeleteHandler = async(e)=>{
        e.preventDefault();
        await axios.delete(`/apis/v1/user/${user_id}`)
        .then(response => {
            console.log(response)
            alert("회원 탈퇴 완료")
            dispatch(logoutAction())
            history.replace('/')

        }).catch(e =>{
            alert("회원 탈퇴 실패")
        })
    }






    return(
        <div>


        <Container>
            <CategoryDirection tag1={'내정보 관리'}></CategoryDirection>
            <br/>
                <Form>
            <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                <Form.Label  column sm="2" lg='1'>
                    ID</Form.Label>
                <Col sm ='10' lg='8'>
                <Form.Control 
                name = 'username'
                value = {username}
                onChange={onChangeHandler}
                placeholder="ID를 입력해주세요" />
                </Col>

            </Form.Group>
            <br/>
            <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                <Form.Label  column sm="2" lg='1' >
                    EMAIL</Form.Label>
                <Col sm ='10' lg='8'>
                <Form.Control 
                name = 'email'
                value = {email}
                onChange={onChangeHandler}
                placeholder="email을 입력해주세요" />
                </Col>

            </Form.Group>
            <br/>
            <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                <Form.Label  column sm="2" lg='1'>PHONE NUMBER</Form.Label>
                <Col sm ='10' lg='8'>
                <Form.Control 
                name = 'phone_number'
                value = {phone_number}
                onChange={onChangeHandler}
                placeholder="전화번호를 입력해주세요" />
                </Col>

            </Form.Group>
            <br/>

            <Button onClick={onClickEditHandler}>수정</Button><span>{' '}</span>
            <Button onClick={onClickDeleteHandler}>회원 탈퇴</Button>
            </Form>


        </Container>
        </div>
    );

}

export default Profile
