import axios from 'axios'
const LOGIN_USER = 'LOGIN_USER'; 
const LOGOUT_USER = 'LOGOUT_USER';
const SET_USER = 'SET_USER';

export const loginAction = (requestData) => {
    return {
      type: LOGIN_USER,
      payload: request,
    };
  };

  export const logoutAction = () => {
    const request = axios.post('/apis/v1/user/logout')
    .then(response => response.data);  

    return {
      type: LOGOUT_USER,
      payload: {}
    };
  };

  const initialState = {
    id: null,
    payload: null,
    isLoggedIn: false
  };

  const user = (state = initialState, action) => {
    switch (action.type) {
      case (LOGIN_USER):
        return ({
          ...state,
          payload: action.payload,
          isLoggedIn: true
        });
      case (LOGOUT_USER):
        return ({
          ...state,
          payload: action.payload,
          isLoggedIn: false
        });
      default:
        return state
    }
  };

  export default user;
