import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {ListGroup, Container} from 'react-bootstrap'
import Title from './Title'
import {useSelector} from 'react-redux'




function OrderList({history}){
    var products = []
    const [orders, setOrders] = useState([]) 
    const {isLoggedIn, userData} = useSelector(state =>({
        isLoggedIn: state.user.isLoggedIn,
        userData: state.user.payload
    }))



    const fetchOrders= async()=>{
            await axios.get('/apis/v1/order/' + userData.user_id).then(res=> {
                console.log(res)
                setOrders(res.data.payload.payload);  


            })



    }



    const fetchProducts= async()=>{
         await axios.get('/apis/v1/product').then(res=> {

            console.log(res.data.payload)
            let tmp = res.data.payload.payload.map(data=> {
                return  {
                    ...data.fields,
                    product_id: data.pk
                }
            })
            console.log(tmp)
            products = tmp
            fetchOrders()
        })
    }


    useEffect(()=>{
        fetchProducts()


    },[userData.user_id])


    return (<div>
        <Title title="구매 목록" set_middle={false}></Title>
        <Container>
        <ListGroup>
            {orders}
        </ListGroup>
        </Container>


    </div>)
}

export default OrderList;
