import React from 'react'
import {useSelector} from 'react-redux'
import {Container, Dropdown, Navbar, Nav, NavDropdown} from 'react-bootstrap'
import {Link} from 'react-router-dom'
function CategoryBanner(){
    const {kind} = useSelector(state => ({
        kind: state.category.payload
    }))
    const display_category = kind.map((k, index) =>{

        let  path = '/category/' + (index + 1)
        return (
            <NavDropdown.Item key={index}><Link to={path} style={{textDecoration:'none', color:'inherit'}}>{k.kind}</Link></NavDropdown.Item>

        );
    }
    );


    return(


                <Navbar bg="primary" variant="dark">
                    <Container>
        <Dropdown>
            <Dropdown.Toggle variant='light' id="dropdown-basic">
                Category
            </Dropdown.Toggle>

            <Dropdown.Menu>
                {display_category}
            </Dropdown.Menu>
        </Dropdown>
        <NavDropdown title="Category" id="navbarScrollingDropdown">
        {display_category}
      </NavDropdown>
        <Nav className="mr-auto">
        <Nav.Link href="#home">판매하기</Nav.Link>
        <Nav.Link href="#features">내 상점</Nav.Link>
        <Nav.Link href="#pricing">장바구니</Nav.Link>

        </Nav>
        </Container>
        </Navbar>




   );
}

export default CategoryBanner
