import {loginAction} from '../modules/user'
import axios from 'axios'
import React, {useState} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {Form, Container, Button, Row, Col, Modal} from 'react-bootstrap'
import { CategoryDirection } from './CategoryBanner'


function Login({history}){
    const [ modalOpen, setModalOpen ] = useState(false);

    const openModal = () => {
        setModalOpen(true);
    }
    const closeModal = () => {
        setModalOpen(false);
    }


    const dispatch = useDispatch();
    const [userData, setUserData] = useState({
        username: "",
        password: ""
    })
    const {username, password} = userData;

    const onChangeHandler = (e) => {
        const {name, value} = e.target;
        setUserData({
            ...userData,
            [name]: value
        })

    };
    const onClickHandler = (e)=>{
        e.preventDefault();
        let body = {
            username: username,
            password: password
        };
        axios.post('/apis/v1/user/login', body)
        .then(response => {
            history.replace('/')
            console.log("login")
            dispatch(loginAction(response.data.payload.payload))
            alert("환영합니다!")
        }).catch(e =>{
            alert("로그인 실패")
            console.log("로그인 실패")
        })
    }

    return (
        <div>

        <React.Fragment>
        <button onClick={ openModal }>모달팝업</button>
        //header 부분에 텍스트를 입력한다.
        <Modal open={ modalOpen } close={ closeModal } header="Modal heading">

            // Modal.js <main>
            <Container >
            <CategoryDirection tag1={'로그인'}></CategoryDirection>
            <br/>
            <div style={{fontSize:'1.3rem'}}>


            <Form onSubmit={onClickHandler} >
            <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                <Col sm={{ span: 2, offset: 2 }} 
                lg={{ span: 1, offset: 3 }}>
                <Form.Label style={{margin:10}}>아이디</Form.Label>

                </Col>
                <Col lg={{ span: 4, offset: 0 }}
                    sm={{ span: 8, offset: 0 }}>
                <Form.Control 
                size='lg'
                name = 'username'
                value = {username}
                onChange={onChangeHandler}
                placeholder="아이디를 입력해주세요" />
                </Col>

            </Form.Group>
            <br/>
            <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                <Col sm={{ span: 2, offset: 2 }} 
                lg={{ span: 1, offset: 3 }}>
                <Form.Label style={{margin:5}}>패스워드</Form.Label>

                </Col>
                <Col lg={{ span: 4, offset: 0 }}
                    sm={{ span: 8, offset: 0 }}>
                <Form.Control
                size='lg'
                type="password"
                name = 'password'
                value = {password} 
                onChange={onChangeHandler}
                placeholder="패스워드를 입력해주세요" />

                </Col>
                </Form.Group>
            <br/>
            <Row>
                <Col lg = {{span:5, offset:3}}
                sm = {{span:10, offset:2}}>
                <div className="d-grid gap-2">
                <button 
                style={{height:50}}
                type="submit"
                className='filledButton'
                >로그인</button>

                </div>

                </Col>
            </Row>

            <Row style={{marginTop:15}}>
                <Col lg = {{span:5, offset:3}}
                sm = {{span:10, offset:2}}>
                <div className="d-grid gap-2">
                <button 
                style={{height:50}}
                className='emptyButton'
                onClick={(e) => {
                    e.preventDefault();
                    alert('회원가입 페이지로 이동합니다')
                    history.replace('/register');
                }}
                >회원가입

                </button>

                </div>

                </Col>
            </Row>
            </Form>
            </div>
        </Container></main>에 내용이 입력된다. 
            리액트 함수형 모달 팝업창입니다.
            쉽게 만들 수 있어요. 
            같이 만들어봐요!

        </Modal>
    </React.Fragment>





        </div>
    );


    // return (<div style ={{
    //     display : 'flex', justifyContent : 'center', alignItems: 'center',
    //     width : '100%', height : '100vh'
    // }}>
    //     <form style ={{display : 'flex', flexDirection:'column'}}
    //         onSubmit={onClickHandler}>
    //         <label>username</label>
    //         <input name = "username" value = {username} onChange={onChangeHandler}/>
    //         <label>Password</label>
    //         <input name = "password" value = {password} onChange={onChangeHandler}/>
    //         <br/>
    //         <button type = 'submit'>
    //             Login
    //         </button>
    //     </form>
    // </div>);
}

export default Login;
