import React, {useEffect, useState} from 'react';
import {useSelector, useDispatch} from 'react-redux'
import {setCategory} from '../modules/category'
import axios from "axios";
import Carousel from "react-bootstrap/Carousel";
import {Button, Container, Row,Col, Card} from 'react-bootstrap'
import Banner from './Banner'
import CategoryBanner from './CategoryBanner'
// import NonLoggedPost from "./NonLoggedPost";




export const DisplayProducts = ({products}) =>{
    products = products.map(product => {
        return (
            <Container>
                <Col xs>
                    <Card style={{ width: '18rem' }}>
                    <Card.Img variant="top" src="holder.js/100px180" />
                    <Card.Body>
                        <Card.Title>{product.name}</Card.Title>
                        <Card.Text>
                        {product.price}원
                        </Card.Text>

                    </Card.Body>
                    </Card>
                </Col>
                </Container>
                )
    })
    return (
        <Row>
        {products}
        </Row>
    )
};

const LoginBanner = ()=>{
    const {isLoggedIn, userData} = useSelector(state =>({
                isLoggedIn: state.user.isLoggedIn,
                userData: state.user.payload
    }))

    if(!isLoggedIn){
        return(
            <div>
            <p>
                로그인하세요!
            </p>
            <Button>로그인 하러가기</Button>
            </div>
        )
    }
    else{
        return(
            <p>
                안녕하세요! {userData.username}
            </p>
        )
    }
}



export default function Home(props){
    const[products,Setproducts]= useState([])

    const dispatch = useDispatch()

    const fetchProducts= async ()=>{
        await axios.get('/apis/v1/product').then(res=> {
            let product_list = res.data.map(data=> {
                return data.fields
            })
            Setproducts(product_list);
        })
    }
    const fetchCategory= async ()=>{
        await axios.get('/apis/v1/category').then(res=> {
            let category_list = res.data.map(data=> {
                return data.fields
            })

            console.log(category_list);
            dispatch(setCategory(category_list))
        })
    }

    useEffect(()=>{
        fetchProducts();
        fetchCategory();
    },[])
        return (
            <div>
               <Banner/>
                <CategoryBanner/>
                <LoginBanner/>



                <div style={{marginTop:70}}>
                <ControlledCarousel/>
                <Container>
                    <DisplayProducts products={products}/>
                </Container>


                </div>
            </div>
        );
}

function ControlledCarousel() {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };
    return (
        <Carousel activeIndex={index} onSelect={handleSelect}>
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://images.unsplash.com/photo-1573612664822-d7d347da7b80?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"
                    alt="First slide"
                    width="800"
                    height="400"
                />
                <Carousel.Caption>
                    <h3>First slide label</h3>
                    <p>First slide label ~~~~</p>
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://i.pinimg.com/originals/b1/68/2d/b1682d7c5579f8ce81b8df8088db43a1.jpg"
                    alt="Second slide"
                    width="800"
                    height="400"
                />

                <Carousel.Caption>
                    <h3>Second slide label</h3>
                    <p>아무거나 적어 </p>
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://img.freepik.com/free-vector/big-fashion-shop-super-market-male-clothes-shopping-mall-interior-banner-with-copy-space_48369-11918.jpg?size=626&ext=jpg"
                    alt="Third slide"
                    width="800"
                    height="400"
                />
                <Carousel.Caption>
                    <h3>Third slide label</h3>
                    <p>
                    세번째
                    </p>
                </Carousel.Caption>
            </Carousel.Item>
        </Carousel>
    );
}
